import axios from "axios";

const SIGNUP = "SIGNUP";

export function signup(data) {
  const req = new Promise((response, reject) => {
    response(axios.post("http://localhost:3065/user/signup", data));
  });

  req
    .then((val) => {
      return {
        type: SIGNUP,
        payload: val,
      };
    })
    .catch((err) => {
      console.error(err);
    });
  // const req = axios
  //   .post("http://localhost:3065/user/signup", data)
  //   .then((res) => res.data);

  // return {
  //   type: SIGNUP_REQ,
  //   payload: req,
  // };
}

const initialState = {
  isLoggedIn: false,
  myInfo: null,
  signupInfo: null,
};

export default function reducer(state = initialState, action) {
  switch (action.type) {
    case SIGNUP:
      return {
        ...state,
        signupInfo: action.payload,
      };
    default:
      return state;
  }
}
