import axios from "axios";

const initialState = {
  signupInfo: null,
  isLoggedIn: false,
  myInfo: null,
};

const SIGNUP = "SIGNUP";

const signupAPI = (formData) => {
  return axios.post("http://localhost:3065/user/signup", formData);
};

export const signupReq = (formData) => async (dispatch) => {
  const res = signupAPI();
  dispatch({
    type: SIGNUP,
    signupInfo: res.data,
  });
};

export default function reducer(state = initialState, action) {
  switch (action.type) {
    case SIGNUP:
      return {
        ...state,
        signupInfo: action.payload,
      };
    default:
      return state;
  }
}
