import React, { useState } from "react";
import { Link } from "react-router-dom";

import "./TopNav.css";

const onClickSearch = () => {};

const TopNav = () => {
  const [showSearch, setShowSearch] = useState("none");
  return (
    <>
      <nav className="mainConatiner navContainer">
        <Link className="go_btn decoNone" to="/post">
          전체
        </Link>
        <Link className="go_btn decoNone" to="/post/hot">
          인기
        </Link>
        <Link className="go_btn decoNone" to="/post/recommend">
          추천
        </Link>
        <div
          className="search_post decoNone"
          onClick={() => {
            setShowSearch("block");
          }}
        >
          검색
        </div>
        <Link className="decoNone" to="/search">
          글쓰기
        </Link>
      </nav>
      <div className="searchPostContainer" style={{ display: showSearch }}>
        <button className="searchOffBtn">닫기</button>
        <input type="text" />
        <div>입력 후 엔터를 눌러주세요</div>
      </div>
    </>
  );
};

export default TopNav;
