export const initialState = {
  signupLoading: false,
  signupDone: false,
  signupInfo: null,
  isLoggedIn: false,
  myInfo: null,
};

export const SIGNUP_REQUEST = "SIGNUP_REQUEST";
export const SIGNUP_SUCCESS = "SIGNUP_SUCCESS";
export const SIGNUP_FAILURE = "SIGNUP_FAILURE";

const reducer = (state = initialState, action) =>
  produce(state, (draft) => {
    switch (action.type) {
      case SIGNUP_REQUEST:
        return {
          ...state,
          signupLoading: true,
          signupError: null,
          signupDone: false,
        };
      case SIGNUP_SUCCESS:
        return {
          ...state,
          signupLoading: false,
          signupDone: true,
        };
      case SIGNUP_FAILURE:
        return {
          ...state,
          signupLoading: false,
          signupError: action.error,
        };
      default:
        return state;
    }
  });

export default reducer;
