import React from "react";
import { useDispatch, useSelector } from "react-redux";

import "./SearchPost.css";

import { SEARCH_TOGGLE } from "../reducer/post";

const SearchPost = () => {
  const dispatch = useDispatch();
  const { searchToggle } = useSelector((state) => state.post);

  return (
    <div className="searchPostContainer" style={{ display: searchToggle }}>
      <button
        className="searchOffBtn"
        onClick={() => {
          dispatch(SEARCH_TOGGLE);
        }}
      >
        닫기
      </button>
      <input type="text" />
      <div>입력 후 엔터를 눌러주세요</div>
    </div>
  );
};

export default SearchPost;
