import axios from "axios";
import { useSelector } from "react-redux";

const SIGNUP = "SIGNUP";

export function signup(data) {
  console.log("data " + data);
  const req = axios
    .post("http://localhost:3065/user/signup", data)
    .then((res) => res.data);

  return {
    type: SIGNUP,
    payload: req,
  };
}

// export const signup = async (data) => {
//   const req = await axios.post("http://localhost:3065/user/signup", data);

//   return {
//     type: SIGNUP,
//     payload: req
//   };
// };

const initialState = {
  isLoggedIn: false,
  myInfo: null,
  signupInfo: null,
};

export default function reducer(state = initialState, action) {
  switch (action.type) {
    case SIGNUP:
      return {
        ...state,
        signupInfo: action.payload,
      };
    default:
      return state;
  }
}
