export function signupReq(data) {
  const req = new Promise((res, rej) => {
    resolve(axois.post("localhost:3065/user/signup", data));
  });
}

export function signup(data) {
  const req = axios
    .post("http://localhost:3065/user/signup", data)
    .then((res) => console.log("res " + req));

  return {
    type: SIGNUP,
    payload: req.data,
  };
  // const req = await axios.post("http://localhost:3065/user/signup", data);

  // return {
  //   type: SIGNUP,
  //   payload: req,
  // };
}

const initialState = {
  isLoggedIn: false,
  myInfo: null,
  signupInfo: null,
};

export default function reducer(state = initialState, action) {
  switch (action.type) {
    case SIGNUP:
      return {
        ...state,
        signupInfo: action.payload,
      };
    default:
      return state;
  }
}
