import axios from "axios";

const SIGNUP = "SIGNUP";

// export const signup = (data) => {
//   const req = axios
//     .post("http://localhost:3065/user/signup", data)
//     .then((res) => res.data);

//   console.log(req);

//   return {
//     type: SIGNUP,
//     payload: req,
//   };
// };

export const signup = async (data) => {
  const req = axios.post("http://localhost:3065/user/signup", data);

  return {
    type: SIGNUP,
    payload: req.data,
  };
};

const initialState = {
  isLoggedIn: false,
  myInfo: null,
  signupInfo: null,
};

export default function reducer(state = initialState, action) {
  switch (action.type) {
    case SIGNUP:
      return {
        ...state,
        signupInfo: action.payload,
      };
    default:
      return state;
  }
}
