import axios from "axios";
import { resolvePath } from "react-router";

const SIGNUP = "SIGNUP";

export async function signup(data) {
  const req = axios.post("http://localhost:3065/user/signup", data);

  // return {
  //   type: SIGNUP,
  //   payload: req,
  // };
}

const initialState = {
  isLoggedIn: false,
  myInfo: null,
  signupInfo: null,
};

export default function reducer(state = initialState, action) {
  switch (action.type) {
    case SIGNUP:
      return {
        ...state,
        signupInfo: action.payload,
      };
    default:
      return state;
  }
}
