import axios from "axios";

const SIGNUP = "SIGNUP";

export function signup(data) {
  const req = axios
    .post("http://localhost:3065/user/signup", data)
    .then((res) => res.data);
  return {
    type: SIGNUP,
    payload: req,
  };
  console.log(req);
}

const initialState = {
  isLoggedIn: false,
  myInfo: null,
  signupError: null,
  signupSuccess: false,
};

export default function reducer(state = initialState, action) {
  switch (action.type) {
    case SIGNUP:
      return {
        ...state,
        signupAlert: action.data,
      };
    default:
      return state;
  }
}
