import axios from "axios";

const SIGNUP = "SIGNUP";

// export function signup(formData) {
//   const req = axios
//     .post("http://localhost:3065/user/signup", formData)
//     .then((res) => res.data);
//   return {
//     type: SIGNUP,
//     payload: req,
//   };
// }

export const signup = (formData) => {
  axios.post("http://localhost:3065/user/signup", formData).then((res) => {
    return {
      type: SIGNUP,
      payload: res.data,
    };
  });
};

const initialState = {
  isLoggedIn: false,
  myInfo: null,
  signupInfo: null,
};

export default function reducer(state = initialState, action) {
  switch (action.type) {
    case SIGNUP:
      return {
        ...state,
        signupInfo: action.payload,
      };
    default:
      return state;
  }
}
