import axios from "axios";

const SIGNUP = "SIGNUP";

export function signup(data) {
  return { type: SIGNUP, data };
}

const signupAPI = async (data) => {
  const req = await axios.post("http://localhost:3065/user/signup", data);
  return { signupInfo };
};

const initialState = {
  isLoggedIn: false,
  myInfo: null,
  signupInfo: null,
};

export default function reducer(state = initialState, action) {
  switch (action.type) {
    case SIGNUP:
      return signupAPI();
    default:
      return state;
  }
}
