export async function signup(data) {
  const req = await axios
    .post("http://localhost:3065/user/signup", data)
    .then((res) => {
      return {
        type: SIGNUP,
        payload: req,
      };
    });
}

const initialState = {
  isLoggedIn: false,
  myInfo: null,
  signupInfo: null,
};

export default function reducer(state = initialState, action) {
  switch (action.type) {
    case SIGNUP:
      return {
        ...state,
        signupInfo: action.payload,
      };
    default:
      return state;
  }
}
