import axios from "axios";

const SIGNUP = "SIGNUP";

function signupAPI(formData) {
  return axios.post("http://localhost:3065/user/signup", formData);
}

export function signup(data) {
  try {
    const req = signupAPI(data);
    return {
      type: SIGNUP,
      payload: req,
    };
  } catch (err) {
    console.error(err);
  }
  // const req = await axios.post("http://localhost:3065/user/signup", formData);
  // return await {
  //   type: SIGNUP,
  //   payload: req,
  // };
}

const initialState = {
  isLoggedIn: false,
  myInfo: null,
  signupInfo: null,
};

export default function reducer(state = initialState, action) {
  switch (action.type) {
    case SIGNUP:
      return {
        ...state,
        signupInfo: action.payload,
      };
    default:
      return state;
  }
}
