import React, { useCallback, useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";

import { signup } from "../reducer/user";
import "./SignUp.css";

const Register = () => {
  const navigate = useNavigate();
  const { signupInfo } = useSelector((state) => state.user);
  const dispatch = useDispatch();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [nickname, setNickname] = useState("");

  useEffect(() => {
    if (signupInfo == "ok") {
      alert("회원가입에 성공했습니다");
      navigate("/");
    }
    if (signupInfo == "alreadyUser") {
      alert("이미 가입된 아이디 입니다");
    }
  }, [signupInfo]);

  const onChangeEmail = useCallback((e) => {
    setEmail(e.target.value);
  }, []);

  const onChangePassword = useCallback((e) => {
    setPassword(e.target.value);
  }, []);

  const onChangeNickname = useCallback((e) => {
    setNickname(e.target.value);
  }, []);

  const onSubmitForm = useCallback(
    (e) => {
      e.preventDefault();
      const formData = {
        email,
        password,
        nickname,
      };
      dispatch(signup(), formData);
    },
    [email, password, nickname]
  );

  return (
    <form className="signUpForm" onSubmit={onSubmitForm}>
      <label>이메일</label>
      <input
        type="email"
        name="email"
        value={email}
        onChange={onChangeEmail}
        className="input_signup"
      />
      <label>비밀번호</label>
      <input
        type="password"
        name="password"
        value={password}
        onChange={onChangePassword}
        className="input_signup"
      />
      <label>닉네임</label>
      <input
        type="nickname"
        name="nickname"
        value={nickname}
        onChange={onChangeNickname}
        className="input_signup"
      />
      <button type="submit" className="signUpBtn" onSubmit={onSubmitForm}>
        가입하기
      </button>
    </form>
  );
};

export default Register;
