import axios from "axios";

const SIGNUP = "SIGNUP";

export async function signup(data) {
  try {
    const req = await axios.post("http://localhost:3065/user/signup", data);
    return {
      type: SIGNUP,
      payload: req,
    };
  } catch (error) {}
}

const initialState = {
  isLoggedIn: false,
  myInfo: null,
  signupError: null,
  signupSuccess: false,
};

export default function reducer(state = initialState, action) {
  switch (action.type) {
    case SIGNUP:
      return {
        ...state,
        signupAlert: action.payload,
      };
    default:
      return state;
  }
}
