import axios from "axios";

const initialState = {
  signupLoading: false,
  signupInfo: null,
  isLoggedIn: false,
  myInfo: null,
};

const SIGNUP = "SIGNUP";
const SIGNUP_SUCCESS = "SIGNUP_SUCCESS";

const signupAPI = (formData) => {
  return axios.post("http://localhost:3065/user/signup", formData);
};

export const signupReq = () => async (dispatch) => {
  dispatch({
    type: SIGNUP,
  });
  try {
    const res = await signupAPI();
    dispatch({
      type: SIGNUP_SUCCESS,
      payload: res.data,
    });
  } catch (e) {
    console.error(e);
  }
};

export default function reducer(state = initialState, action) {
  switch (action.type) {
    case SIGNUP:
      return {
        ...state,
        signupLoading: true,
      };
    case SIGNUP_SUCCESS:
      return {
        ...state,
        signupLoading: false,
        signupInfo: action.payload,
      };
    default:
      return state;
  }
}
