import axios from "axios";

const SIGNUP = "signup";

export function signup(data) {
  const req = axios.post("localhost:3065/user/signup", data).then((res) => res.data);
  return {
    type: SIGNUP,
    payload: req,
  };
}

const initialState = {
  isLoggedIn: false,
  myInfo: null,
};

export default function reducer(state = initialState, action) {
  switch (action.type) {
    case SIGNUP:
      return {
        ...state,
        signupSuccess: action.payload,
      };
    default:
      return state;
  }
}
