import axios from "axios";

const SIGNUP = "SIGNUP";

// export function signup(data) {
//   const req = axios.post("http://localhost:3065/user/signup", data);

//   return {
//     type: SIGNUP,
//     payload: req,
//   };
// }

export const signup = async (data) => {
  const req = await axios.post("http://localhost:3065/user/signup", data);
  const data2 = await req.json();

  return {
    type: SIGNUP,
    payload: JSON.parse(data2),
  };
};

const initialState = {
  isLoggedIn: false,
  myInfo: null,
  signupInfo: null,
};

export default function reducer(state = initialState, action) {
  switch (action.type) {
    case SIGNUP:
      return {
        ...state,
        signupInfo: action.payload,
      };
    default:
      return state;
  }
}
