import axios from "axios";

const initialState = {
  signupLoading: false,
  signupDone: null,
  signupError: null,
  isLoggedIn: false,
  myInfo: null,
};

const SIGNUP_REQUEST = "SIGNUP_REQUEST";
const SIGNUP_SUCCESS = "SIGNUP_SUCCESS";
const SINGUP_FAILURE = "SIGNUP_FAILURE";

const signupAPI = async (formData) => {
  const req = await axios.post("http://localhost:3065/user/signup", formData);

  return req;
};

export const signupReq = (formData) => async (dispatch) => {
  dispatch({ type: SIGNUP_REQUEST });
  console.log("SIGNUP");
  try {
    const req = await signupAPI(formData);
    console.log("req" + req);
    dispatch({
      type: SIGNUP_SUCCESS,
      signupDone: req.data,
    });
  } catch (err) {
    dispatch({
      type: SINGUP_FAILURE,
      signupError: err,
    });
  }
};

export default function reducer(state = initialState, action) {
  switch (action.type) {
    case SIGNUP_REQUEST:
      return {
        ...state,
        signupLoading: true,
        signupError: null,
        signupDone: false,
      };
    case SIGNUP_SUCCESS:
      return {
        ...state,
        signupLoading: false,
        signupDone: true,
      };
    case SINGUP_FAILURE:
      return {
        ...state,
        signupLoading: false,
        signupError: action.signupError,
      };
    default:
      return state;
  }
}
